<?php

namespace OpinionBundle\Repository;

/**
 * OpinionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class OpinionRepository extends \Doctrine\ORM\EntityRepository
{
    //CALCULER LA MOYENNE
    public function MoyNoteOpinion()
    {
        $query=$this->getEntityManager()
            ->createQuery("select SUM(o.note),COUNT(o.note)from OpinionBundle:Opinion as o ");
        return $query->getSingleResult();

    }
    public function NbrNoteOpinion()
    {
        $query=$this->getEntityManager()
            ->createQuery("select COUNT(o.idOp) as nbr from OpinionBundle:Opinion as o group by o.note order by o.note DESC ");
        return $query->getResult();

    }
    public function statOpinion()
    {
        $query=$this->getEntityManager()
            ->createQuery("select COUNT(o.idOp) as nbr ,o.note as note from OpinionBundle:Opinion as o group by o.note ");
        return $query->getResult();

    }




}
